import React, { Component } from "react";

class Shopify extends Component {

  constructor(props) {
    super(props);     //inherit from React.Component

    this.state = {
      api: "",
      password: "",
      shop: "",
    };
  }   

  changeAPI(event) {
    this.setState({
      api: event.target.value
    });
  }

  changePassword(event) {
    this.setState({
      password: event.target.value
    });
  }

  changeShop(event) {
    this.setState({
      shop: event.target.value
    });
  }

  render() {
    console.log(this.state);
    return( //props of this Component, which one? name
      //to write Javascript inside HTML, use {}
        <section id="hello-world">
          <h2>Shopify Feed URL Builder</h2>
          <label>API Key</label><br/>
          <input value={this.state.api} onChange={this.changeAPI.bind(this)}/>    
          <br/>
          <br/>
          <label>Password</label><br/>
         <input value={this.state.password} onChange={this.changePassword.bind(this)}/>
          <br/>
          <br/>
          <label>Shop URL</label><br/>
         <input value={this.state.shop} onChange={this.changeShop.bind(this)}/>


         <h3>
         https://{this.state.api}:{this.state.password}@{this.state.shop}.myshopify.com/admin/feed.json
        </h3> 
        </section>
    );
  }
}

export default Shopify;
