import React, { Component } from "react";
import {
  Route,
  NavLink,
  HashRouter
} from "react-router-dom";
import Home from "./Home";
import Shopify from "./Shopify";
import Magento from "./Magento";
import BigCommerce from "./BigCommerce"

class Main extends Component {
  render() {
    return (
        <HashRouter>
            <div>
                <h1>Simple SPA</h1>
                <ul className="header">
                    <li><NavLink exact to="/">Home</NavLink></li>
                    <li><NavLink to="/shopify">Shopify</NavLink></li>
                    <li><NavLink to="/magento">Magento</NavLink></li>
                    <li><NavLink to="/bigcommerce">Big Commerce</NavLink></li>
                </ul>
                <div className="content">
                    <Route exact path="/" component={Home}/>
                    <Route path="/shopify" component={Shopify}/>
                    <Route path="/magento" component={Magento}/>                    
                    <Route path="/bigcommerce" component={BigCommerce}/> 
                </div>
            </div>
        </HashRouter>
    );
  }
}

export default Main;
