import React, { Component } from "react";

class patientlog extends Component {

  constructor() {
    super();

    this.displayData = [];

    this.state = {
      showdata : this.displayData,
      postVal : ""
    }

    this.appendData = this.appendData.bind(this);
    this.handleChange = this.handleChange.bind(this);

  };

appendData() {
  this.displayData.push(<div d="display-data"><pre>{this.state.postVal}</pre></div>);
  this.setState({
    showdata : this.displayData,
    postVal : ""
  });
}

clearData() {
  this.displayData = [];
}

handleChange(e) {
  let getTextAreaValue = e.target.value;
  this.setState({
    postVal :getTextAreaValue
  });
}

render() {
  return (
        <div id="mainContainer">

          <div id="display-data-Container">
            <p>{this.displayData}</p>
          </div>

          <textarea rows="10" cols="100" placeholder="Enter log entry" value={this.state.postVal} onChange={this.handleChange} ></textarea>

          <div>
            <input type="submit" className="button" onClick={this.appendData}  value="Submit"/>
            <input type="button" className="button" onClick={this.clearData}  value="Clear"/>
          </div>

        </div>
    );
  } 
}

export default patientlog;
