import React, { Component } from "react";

class patientlog extends Component {

  constructor() {
    super();

    this.displayData = [];

    this.state = {
      showdata : this.displayData,
      postVal : ""
    }

    this.appendData = this.appendData.bind(this);
    this.prependData = this.prependData.bind(this);
    this.handleChange = this.handleChange.bind(this);

  };

appendData() {
       this.displayData.push(<div  id="display-data"><pre>{this.state.postVal}</pre></div>);
       this.setState({
          showdata : this.displayData,
          postVal : ""
       });
    }

handleChange(e) {
    let getTextAreaValue = e.target.value;
    this.setState({
      postVal :getTextAreaValue
    });
}

render() {
  return (
        <div id="mainContainer">

          <div id="display-data-Container">
            {this.displayData}
          </div>

          <textarea rows="4" cols="50" value={this.state.postVal} onChange={this.handleChange} ></textarea>
          <div>
           <input type="submit" className="button" onClick={this.appendData}  value="Submit"/>
           </div>

        </div>
    );
}
}

export default patientlog;
