import React, { Component } from "react";

class patientlog extends Component {

  constructor(props) {
    super(props);     //inherit from React.Component

    this.state = {
      text: "",
      log: ""
    };
  }  

  changeText(event) {
    this.setState({
      text: event.target.value
    });
      log = log + text
  }

  render() {
    console.log(this.state);
    return( 
      <div>
          <div>
            {this.state.text}
          </div>

          <textarea value={this.state.text} onChange={this.changeText.bind(this)} maxlength="2000" rows="4" cols="50" placeholder="Enter log entry">

          </textarea> 
      </div>    
    );
  }
}

export default patientlog;
